import React, { Component } from 'react'
import { Link } from 'react-router-dom'
export default class Recipe extends Component {

    addQuantity = (id) => {
        this.props.handleAddQuantity(id);
    }

    subtractQuantity = (id) => {
        this.props.handleSubtractQuantity(id);
    }

    render() {
        const { item } = this.props;
        return (
            <div>
                <Link to="/"><svg onClick={() => { this.handleSubtractQuantity(id) }} width="24" height="24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="current-stroke c-blue c-pointer"><circle cx="12" cy="12" r="10"></circle><path d="M8 12h8"></path></svg></Link>
                <div className="addcart"><input type="text" value={item.quantity} min="1" className="productquantity" /></div>
                <Link to="/"><svg onClick={() => { this.handleAddQuantity(item.id) }} width="24" height="24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="current-stroke c-blue c-pointer"><circle cx="12" cy="12" r="10"></circle><path d="M12 8v8M8 12h8"></path></svg></Link>
            </div>
        )
    }
}
